CREATE TABLE web_type_user (id_web_type_user int(10) NOT NULL auto_increment,name_web_type_user varchar(100) default NULL,PRIMARY KEY (id_web_type_user));CREATE TABLE web_user (id_web_user int(10) NOT NULL auto_increment,name_web_user varchar(100) default NULL,last_name_web_user varchar(100) default NULL,email_web_user varchar(100) default NULL,id_type_web_user int(10) NOT NULL,date_access_web_user datetime default NULL,ip_access_web_user varchar(50) default NULL,PRIMARY KEY (id_web_user),KEY FK_web_user (id_type_web_user),CONSTRAINT FK_web_userFOREIGN KEY (id_type_web_user) REFERENCES web_type_user (id_web_type_user)ON DELETE CASCADE);